import bg11Image from 'assets/img/bg/background_1920-11.jpg';
import bg18Image from 'assets/img/bg/background_1920-18.jpg';
import bg1Image from 'assets/img/bg/background_640-1.jpg';
import bg3Image from 'assets/img/bg/background_640-3.jpg';
import raspImage from 'assets/img/products/rasp.jpg';
import { UserCard } from 'components/Card';
import Page from 'components/Page';
import { bgCards, gradientCards, overlayCards } from 'demos/cardPage';
import { getStackLineChart, stackLineChartOptions } from 'demos/chartjs';
import React from 'react';
import PropTypes from 'prop-types';
import { Line } from 'react-chartjs-2';
import axios from 'axios';
import { Link, withRouter } from 'react-router-dom'
import {
  Button,
  Card,
  CardBody,
  CardImg,
  CardImgOverlay,
  CardLink,
  CardText,
  CardTitle,
  Col,
  ListGroup,
  ListGroupItem,
  Modal,
  ModalBody,
  ModalFooter,
  ModalHeader,
  DropdownItem,
  DropdownMenu,
  DropdownToggle,
  Row,
  Form,
  FormFeedback,
  FormGroup,
  FormText,
  Input,
  Label,
  UncontrolledButtonDropdown,
} from 'reactstrap';
import { getAllModels, getCards } from 'components/UserFunctions';
class ReservationPage extends React.Component {
  constructor() {
    super()
    this.state = {
      models: [],
      cards: [],
      isCloseCard: true,
      isNotificationConfirmed: false,
      isCloseDate: true,
      button: "Modéle de la carte",
      value: '',

    }
    this.onChange = this.onChange.bind(this)
 //   this.handleSubmit = this.handleSubmit.bind(this)
  }
  onChange(e) {
    this.setState({ [e.target.name]: e.target.value })
    this.loadCards(e.value)
  }


  loadCards = idModel => event => {
    event.preventDefault();

    getCards(idModel).then(res => {
      const cards = res;
      this.setState({ cards });
    });
    this.setState({
      isCloseCard: false,
    });
    this.setState({
      isCloseDate: false,
    });
  };
  componentDidMount() {
    /*  let models = [...this.state.model]
      getAllModels().then(data =>  models.push(data),
      this.setState({
        model: models
      }))*/
    getAllModels()
      .then(res => {
        const models = res;
        this.setState({ models });
      })

  }

  render() {
    return (
      <Page title="Réservation" breadcrumbs={[{ name: 'Réservation', active: true }]}>
        <Row>
          <Col md={6} sm={6} xs={12} className="mb-3">
            <Card>
              <CardBody>
                <CardTitle>Vous Pouvez ici réserver une carte</CardTitle>
                <CardText>
                  Contenu
              </CardText>
              </CardBody>
            </Card>
          </Col>
        </Row>
        <Card>
          <CardBody>
            <Form>
              <Row>

                <Col md={6} sm={6} xs={12} className="mb-3">
                  <Card>
                    <CardBody>
                      <CardTitle>Choisir Un Modéle</CardTitle>
                      <FormGroup row>
                        <Label for="exampleSelect" sm={2}>
                          Modéle
                  </Label>
                        <Col sm={10}>
                          <select color="primary" >
                            {this.state.models.map(model => {

                              return (
                                <option onClick={this.loadCards(model.id)} onChange={this.loadCards(model.id)}>{model.name}</option>
                              );
                            })}
                          </select>
                        </Col>
                      </FormGroup>
                    </CardBody>
                  </Card>
                </Col>
                <Col md={6} sm={6} xs={12} className="mb-3">
                  <Card>
                    <CardBody>
                      <CardTitle>Choisir La durée de réservation </CardTitle>
                      <FormGroup row>
                        <Label for="exampleSelect" sm={2}>
                          Durée
                  </Label>
                        <Col sm={10}>
                          <Input type="select" name="select" color="primary" >
                            <option>30 minutes</option>
                            <option>1 heure </option>
                            <option>1 heure et demi</option>
                            <option>2 heures</option>
                            <option>3 heures</option>
                            <option>4 heures</option>
                          </Input>
                        </Col>
                      </FormGroup>
                      <FormGroup check row>
                        <Col sm={{ size: 10, offset: 2 }}>

                        </Col>
                      </FormGroup>
                    </CardBody>
                  </Card>
                </Col>
              </Row>
              <Row>

              </Row>
              <Col md={8} sm={8} xs={12} className="mb-3" hidden={this.state.isCloseCard}>
                <Card>
                  <CardBody>
                    <CardTitle>Choisir Une Carte </CardTitle>
                    <FormGroup row>
                      <Label for="exampleSelect" sm={2}>
                        Cartes
                  </Label>
                      <Col sm={10}>
                        <Input type="select" name="select" color="primary">
                          {this.state.cards.map(card => {

                            return (
                              <option>{card.name}</option>
                            );
                          })}
                        </Input>
                      </Col>
                    </FormGroup>
                  </CardBody>
                </Card>
              </Col>
              <Button color="primary">Confirmer</Button>
            </Form>
          </CardBody>
        </Card>
      </Page>
    );
  }
}

export default ReservationPage;
