import Page from 'components/Page';
import React from 'react';
import { Link, withRouter } from 'react-router-dom'
import { getCards, deleteCard } from 'components/UserFunctions';
import {
    Button,
    Card,
    CardBody,
    CardHeader,
    Col,
    Modal,
    ModalBody,
    ModalFooter,
    ModalHeader,
    Row, Table
} from 'reactstrap';
class CardList extends React.Component {
    constructor() {
        super()
        this.state = {
            cards: [],
            modal: false,
            modal_backdrop: false,
            modal_nested_parent: false,
            modal_nested: false,
            backdrop: true,
        }
    }

    toggle = modalType => () => {
        if (!modalType) {
            return this.setState({
                modal: !this.state.modal,
            });
        }

        this.setState({
            [`modal_${modalType}`]: !this.state[`modal_${modalType}`],
        });

    };
    onDelete(id) {
        const cardId = id
        deleteCard(cardId).then(res => {
            window.location.reload();
        })
    }
    componentDidMount() {
        getCards()
            .then(res => {
                const cards = res;
                this.setState({ cards });
            })
    }


    render() {
        return (
            <Page title="Cartes" breadcrumbs={[{ name: 'Liste Cartes', active: true }]}>

                <Row>
                    <Col>
                        <Card className="mb-3">
                            <CardHeader>IRTL Cards</CardHeader>
                            <CardBody>
                                <Table striped>
                                    <thead>
                                        <tr>
                                            <th>#</th>
                                            <th>Nom</th>
                                            <th>Date d'ajout</th>
                                            <th>Statut</th>
                                            <th></th>
                                            <th></th>
                                        </tr>
                                    </thead>
                                    <tbody>

                                        {this.state.cards.map(user => {

                                            return (
                                                <tr>
                                                    <th scope="row"></th>
                                                    <td>{user.name}</td>
                                                    <td>{user.date}</td>
                                                    <td>{user.status ? "Disponible" : "Indisponible"}</td>
                                                    <td><Button color="danger" onClick={this.toggle()}>Supprimer</Button></td>
                                                    <td><Button color="info">Editer</Button></td>
                                                    <Modal
                    isOpen={this.state.modal}
                    toggle={this.toggle()}
                    className={this.props.className}>
                    <ModalHeader toggle={this.toggle()} style={{color: "red" }}>Avertissement</ModalHeader>
                    <ModalBody>
                     Vous êtes sur le point de supprimer la carte {user.name}, cette action est irréversible
                  </ModalBody>
                    <ModalFooter>
                      
                        <Button color="primary" onClick={() => this.onDelete(user._id)}>
                          confirmer
                    </Button>
                      <Button color="secondary" onClick={this.toggle()}>
                        Annuler
                    </Button>
                    </ModalFooter>
                  </Modal>
                                                </tr>);
                                        })}

                                    </tbody>
                                </Table>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>    <Link to="/addCard">
                    <Button color="primary" >Ajouter une Carte</Button></Link>
            </Page>
        );
    }
}

export default CardList;
