import logo200Image from 'assets/img/logo/logo_200.png';
import PropTypes from 'prop-types';
import React from 'react';
import { Button, Form, FormGroup, Input, Label } from 'reactstrap';
import { register } from './UserFunctions';
import { login, getRole, getState } from './UserFunctions';
import { withRouter } from "react-router-dom";
import jwt_decode from 'jwt-decode';
class AuthForm extends React.Component {
  constructor() {
    super()
    this.state = {
      first_name: '',
      last_name: '',
      email: '',
      password: '',
      class: '',
      emailValid: false,
      passwordValid: false,
      formValid: false,
      classValid: false,
      errors: {
        email: '',
        password: '',
        class: '',
        first_name: '',
        last_name: '',
      },
    }

    this.onChange = this.onChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  get isLogin() {
    return this.props.authState === STATE_LOGIN;
  }

  get isSignup() {
    return this.props.authState === STATE_SIGNUP;
  }


  onChange(e) {
    this.setState({ [e.target.name]: e.target.value })
  }

  handleChange = (event) => {
    event.preventDefault();
    const { name, value } = event.target;
    let errors = this.state.errors;

    switch (name) {
      case 'first_name': 
        errors.fullName = 
        nameRegex.test(value)
            ? 'Full Name must be 5 characters long!'
            : '';
        break;
        case 'last_name': 
        errors.fullName = 
        nameRegex.test(value)
            ? 'Full Name must be 5 characters long!'
            : '';
        break;
      case 'email': 
        errors.email = 
          validEmailRegex.test(value)
            ? ''
            : 'Email is not valid!';
        break;
      case 'password': 
        errors.password = 
          value.length < 6
            ? 'Password must be 6 characters long!'
            : '';
        break;
        case 'class': 
        errors.email = 
          classRegex.test(value)
            ? ''
            : 'Class is not valid!';
        break;
      default:
        break;
    }

    this.setState({errors, [name]: value});
  }
  changeAuthState = authState => event => {
    event.preventDefault();

    this.props.onChangeAuthState(authState);
  };

  handleSubmit = event => {
    event.preventDefault();
    const newUser = {
      first_name: this.state.first_name,
      last_name: this.state.last_name,
      email: this.state.email,
      password: this.state.password,
      class: this.state.class
    }
    const user = {
      email: this.state.email,
      password: this.state.password
    }
    if(validateForm(this.state.errors)) 
    {
      this.isSignup ? register(newUser).then(res => {
        //this.changeAuthState(STATE_LOGIN)
        this.props.history.push(`/login`)
      }) : login(user).then(res => {
        console.log(getRole(user))
        //if(getRole(user.email)=="admin")
        getRole(user)
        if (res) {

          // this.props.history.push(`/administration`)
          // else
          this.props.history.push(`/home`)
        }
      })
    }
    else{
      console.error('Invalid Form')
    }

  };

  renderButtonText() {
    const { buttonText } = this.props;

    if (!buttonText && this.isLogin) {
      return 'Connexion';
    }

    if (!buttonText && this.isSignup) {
      return 'Inscription';
    }

    return buttonText;
  }

  render() {
    const {
      showLogo,
      firstnameLabel,
      firstnameInputProps,
      lastnameLabel,
      lastnameInputProps,
      usernameLabel,
      usernameInputProps,
      passwordLabel,
      passwordInputProps,
      confirmPasswordLabel,
      confirmPasswordInputProps,
      classLabel,
      classInputProps,
      children,
      onLogoClick,
    } = this.props;

    return (
      <Form onSubmit={this.handleSubmit} noValidate>
        {showLogo && (
          <div className="text-center pb-4">
            <img
              src={logo200Image}
              className="rounded"
              style={{ width: 60, height: 60, cursor: 'pointer' }}
              alt="logo"
              onClick={onLogoClick}
            />
          </div>
        )}

        <FormGroup>
          <Label for={usernameLabel}>{usernameLabel}</Label>
          <Input
            type="email"
            name="email"
            placeholder="Exp@email.com"
            value={this.state.email}
            onChange={this.handleChange}
          noValidate/>
           {errors.email.length > 0 && 
                <span className='error'>{errors.email}</span>}
        </FormGroup>
        <FormGroup>
          <Label for={passwordLabel}>{passwordLabel}</Label>
          <Input type="password"
            name="password"
            placeholder="Votre Mot de passe"
            value={this.state.password}
            onChange={this.handleChange} noValidate/>
             {errors.password.length > 0 && 
                <span className='error'>{errors.password}</span>}
        </FormGroup>
        {this.isSignup && (
          <FormGroup>
            <Label for={confirmPasswordLabel}>{confirmPasswordLabel}</Label>
            <Input type="password"
              name="password"
              placeholder="Confirmer Votre mot de passe"
            />
            
          </FormGroup>

        )}
        {this.isSignup && (
          <FormGroup>
            <Label for={firstnameLabel}>{firstnameLabel}</Label>
            <Input type="text"
              name="first_name"
              placeholder=" Votre Prénom"
              value={this.state.first_name}
              onChange={this.handleChange} noValidate />
               {errors.first_name.length > 0 && 
                <span className='error'>{errors.first_name}</span>}
          </FormGroup>
        )}
        {this.isSignup && (
          <FormGroup>
            <Label for={lastnameLabel}>{lastnameLabel}</Label>
            <Input type="text"
              name="last_name"
              placeholder=" Votre Nom"
              value={this.state.last_name}
              onChange={this.handleChange} noValidate/>
               {errors.last_name.length > 0 && 
                <span className='error'>{errors.last_name}</span>}
          </FormGroup>


        )}
        {this.isSignup && (
          <FormGroup>
            <Label for={classLabel}>{classLabel}</Label>
            <Input type="text"
              name="class"
              placeholder="Votre Classe (exp 1A1)"
              value={this.state.class}
              onChange={this.handleChange} noValidate/>
               {errors.class.length > 0 && 
                <span className='error'>{errors.class}</span>}
          </FormGroup>

        )}
        <FormGroup check>
          <Label check>
            <Input type="checkbox" />{' '}
            {this.isSignup ? 'Acceptez les termes et la politique' : 'Se souvenir de moi'}
          </Label>
        </FormGroup>
        <hr />
        <Button
          size="lg"
          className="bg-gradient-theme-left border-0"
          block
          onClick={this.handleSubmit}>
          {this.renderButtonText()}
        </Button>

        <div className="text-center pt-1">
          <h6>Ou</h6>
          <h6>
            {this.isSignup ? (
              <a href="#login" onClick={this.changeAuthState(STATE_LOGIN)}>
                Connexion
              </a>
            ) : (
                <a href="#signup" onClick={this.changeAuthState(STATE_SIGNUP)}>
                  Inscription
                </a>
              )}
          </h6>
        </div>

        {children}
      </Form>
    );
  }
}

