import { AnnouncementCard, TodosCard } from 'components/Card';
import HorizontalAvatarList from 'components/HorizontalAvatarList';
import MapWithBubbles from 'components/MapWithBubbles';
import Page from 'components/Page';
import ProductMedia from 'components/ProductMedia';
import SupportTicket from 'components/SupportTicket';
import UserProgressTable from 'components/UserProgressTable';
import { IconWidget, NumberWidget } from 'components/Widget';
import { getStackLineChart, stackLineChartOptions } from 'demos/chartjs';
import jwt_decode from 'jwt-decode';
import {
  avatarsData,
  chartjs,
  productsData,
  supportTicketsData,
  todosData,
  userProgressTableData,
} from 'demos/dashboardPage';
import React from 'react';
import { Route, Link, withRouter, Redirect } from 'react-router-dom';
import { Bar, Line } from 'react-chartjs-2';
import {
  MdBubbleChart,
  MdInsertChart,
  MdPersonPin,
  MdPieChart,
  MdRateReview,
  MdShare,
  MdShowChart,
  MdThumbUp,
} from 'react-icons/md';
import InfiniteCalendar from 'react-infinite-calendar';
import {
  Badge,
  Button,
  Card,
  CardBody,
  CardDeck,
  CardGroup,
  CardHeader,
  CardTitle,
  Col,
  ListGroup,
  ListGroupItem,
  Row,
  Form,
  FormGroup,
  FormText,
  Input,
  Label,
  CardText,
} from 'reactstrap';
import { getColor } from 'utils/colors';
import Image from 'assets/img/bg/IoT.jpeg';
import { addRec,getMyReservation } from 'components/UserFunctions';
const validateForm = (errors) => {
  let valid = true;
  Object.values(errors).forEach(
    (val) => val.length > 0 && (valid = false)
  );
  return valid;
}

const today = new Date();
const lastWeek = new Date(
  today.getFullYear(),
  today.getMonth(),
  today.getDate() - 7,
);

class DashboardPage extends React.Component {
  constructor() {
    super()
    this.state = {
      reservation:[],
      objet: '',
      rec: '',
      email: '',
      errors: {
        objet: '',
        rec: '',
      }
    }
    this.handleSubmit = this.handleSubmit.bind(this)
  }


  handleChange = (event) => {
    event.preventDefault();
    const { name, value } = event.target;
    let errors = this.state.errors;

    switch (name) {
      case 'objet':
        errors.objet =
        value.length > 3
            ? ''
            : 'Vérifier votre Objet !';
        break;
      case 'rec':
        errors.rec =
        value.length > 5
            ? ''
            : 'Contenu Réclamation ne doit pas être vide !';
        break;
      default:
        break;
    }
    this.setState({ errors, [name]: value });
  }
  isEmpty() {
    if (this.state.objet !== '' && this.state.rec !== '')
      return true
    else
      return false
  }

  handleSubmit = event => {
    event.preventDefault();
    const token = localStorage.usertoken
    const decoded = jwt_decode(token)
    const newRec = {
      user: decoded._id,
      name: this.state.objet,
      description: this.state.rec,

    }
    if (validateForm(this.state.errors) && this.isEmpty()) {
      addRec(newRec).then(res => {
        window.location.reload();
      })
    }

  };
  componentDidMount() {
    // this is needed, because InfiniteCalendar forces window scroll
    const token = localStorage.usertoken
    const decoded = jwt_decode(token)
    this.setState({
      first_name: decoded.first_name,
      last_name: decoded.last_name,
      email: decoded.email
    })
    window.scrollTo(0, 0);
    getMyReservation().then(res => {
      const reservation = res;
      this.setState({ reservation: res });
    })
  }
  logOut(e) {
    e.preventDefault()
    localStorage.removeItem('usertoken')
    this.props.history.push(`/login`)
    
  }

  render() {
    const primaryColor = getColor('primary');
    const secondaryColor = getColor('secondary');
  

    return (
      
      <Page
        className="DashboardPage"
        title="Dashboard"
        breadcrumbs={[{ name: 'Dashboard', active: true }]}
      >
        
        <Row>   <Col lg="10" md="12" sm="12" xs="12">
            <AnnouncementCard
              color="gradient-secondary"
              header="Bienvenu Dans IOT REMOTE TEST LAB IRTL"
              text="Pour le moment vous ne possédez pas aucune réservation "
              buttonProps={{
                children: 'Commencer',
              }}
              style={{ height: 500 }}
            />
          </Col></Row>

        <Row>
          <Col md="6" sm="12" xs="12">
          <TodosCard image={Image}  title="Cartes" to="/cards"
          />
          </Col>


    
          <Col lg="6" md="12" sm="12" xs="12">
            <TodosCard  />
          </Col>
          </Row>
        <Row>

        
          
          <Col  lg="10" md="12" sm="12" xs="12">
            <Card>
              <CardHeader>Réclamation </CardHeader>
              <CardBody>
                <CardText>
                  Ici vous pouvez faire une réclamation concernant , le fonctionnement des cartes , une panne quelconque , ou même
                  une remarque afin d'améliorer la plateforme
                </CardText>
                <Form onSubmit={this.handleSubmit} noValidate >
          
              <FormGroup row>
                <Label for="exampleEmail" sm={2}>
                  Objet
                      </Label>
                <Col sm={10}>
                  <Input
                    type="text"
                    name="objet"
                    value={this.state.objet}
                    onChange={this.handleChange}
                    noValidate
                  />
                  {this.state.errors.objet.length > 0 &&
                    <span style={{ fontSize: 12, color: "red" }}>{this.state.errors.bjet}</span>}
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label for="exampleEmail" sm={2}>
                  Votre Réclamation
                      </Label>
                <Col sm={10}>
                  <textarea style={{ width: 600,height: 200 }}
                    type="text"
                    name="rec"
                    value={this.state.rec}
                    onChange={this.handleChange}
                    noValidate
                  />
                  {this.state.errors.rec.length > 0 &&
                    <span style={{ fontSize: 12, color: "red" }}>{this.state.errors.rec}</span>}
                </Col>
              </FormGroup>
              <FormGroup check row>
                <Col sm={{ size: 10, offset: 2 }}>
                  <Button>Envoyer</Button>
                </Col>
              </FormGroup>
            </Form>
              </CardBody>
            </Card>
          </Col>
        </Row>
       
      </Page>
    );
  }
}
export default withRouter(DashboardPage);
