import Avatar from 'components/Avatar';
import { UserCard } from 'components/Card';
import Notifications from 'components/Notifications';
import user1Image from 'assets/img/users/100_1.jpg';
import SearchInput from 'components/SearchInput';
//import { notificationsData } from 'demos/header';
import withBadge from 'hocs/withBadge';
import React from 'react';
import { Link, withRouter } from 'react-router-dom';
import jwt_decode from 'jwt-decode';
import { getMyResponses } from 'components/UserFunctions';
import axios from 'axios';
import moment from 'moment';
import {
  MdClearAll,
  MdExitToApp,
  MdKeyboardArrowDown,
  MdHelp,
  MdInsertChart,
  MdMessage,
  MdExtension,
  MdNotificationsActive,
  MdNotificationsNone,
  MdPersonPin,
  MdSettingsApplications,
  MdRadioButtonChecked,
  MdChromeReaderMode,
} from 'react-icons/md';
import {
  Button,
  ListGroup,
  ListGroupItem,
  // NavbarToggler,
  Nav,
  Navbar,
  NavItem,
  NavLink,
  Popover,
  PopoverBody,
  Collapse,
  NavLink as BSNavLink,
} from 'reactstrap';
import bn from 'utils/bemnames';

const bem = bn.create('header');
const navComponents = [
  { to: '/cards', name: 'consulter les cartes', exact: false, Icon: MdRadioButtonChecked },

  { to: '/reservation', name: 'reservation', exact: false, Icon: MdChromeReaderMode },
];
const MdNotificationsActiveWithBadge = withBadge({
  size: 'md',
  color: 'primary',
  style: {
    top: -10,
    right: -10,
    display: 'inline-flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  children: <small>5</small>,
})(MdNotificationsActive);

class Header extends React.Component {
  state = {
    isOpenNotificationPopover: false,
    isNotificationConfirmed: false,
    isOpenUserCardPopover: false,
    first_name: '',
    last_name: '',
    email: '',
    password: '',
    profileImg: '',
    userId: '',
    data: [],
    notificationsData: [],
    errors: {}
  };
  checkDate(duration) {
    if (duration.hours() == 0 && duration.days() == 0)
      return duration.minutes() + " M"
    else if (duration.days() == 0)
      return duration.hours() + " H"
    else
      return duration.days() + " J"


  }
  handleClick = name => () => {
    this.setState(prevState => {
      const isOpen = prevState[`isOpen${name}`];

      return {
        [`isOpen${name}`]: !isOpen,
      };
    });
  };
  componentDidMount() {
    const token = localStorage.usertoken
    const decoded = jwt_decode(token)
    this.setState({
      userId: decoded._id,
      first_name: decoded.first_name,
      last_name: decoded.last_name,
      email: decoded.email,
      password: decoded.password,
      profileImg: decoded.profileImg

    })
    let user = decoded._id
    axios.get(`/responses/getMyResponses/${user}`)
      .then(res => {
        const data = res.data.responses;
        this.setState({ data: res.data.responses });
        console.log(this.state.data)
        this.state.data.forEach(e => {
          let diff = moment(new Date()).diff(moment(e.date))
          const object = {
            _id: e._id,
            description: e.description,
            dateRes: e.date,
            id: data.indexOf(e) + 1,
            avatar: user1Image,
            message: e.name,
            date: "Il y a" + this.checkDate(moment.duration(diff)),
          }
          this.state.notificationsData.push(object)
          console.log(this.state.notificationsData)
        })

      })
     this. MdNotificationsActiveWithBadge = withBadge({
        size: 'md',
        color: 'primary',
        style: {
          top: -10,
          right: -10,
          display: 'inline-flex',
          justifyContent: 'center',
          alignItems: 'center',
        },
      children: <small>{this.state.notificationsData.length}</small>,
      })(MdNotificationsActive);
    // console.log(user)
    // console.log(this.state.data)

  }
  isUser = () => {
    let user = false
    // console.log(localStorage.getItem('role'))
    if (localStorage.getItem('role') === 'user')
      user = true
    return user;
  };
  logOut(e) {
    e.preventDefault()
    // localStorage.removeItem('usertoken')
    // localStorage.removeItem('admin')
    // localStorage.removeItem('state')
    localStorage.clear()
    this.props.history.push(`/login`)
  }
  toggleNotificationPopover = () => {
    this.setState({
      isOpenNotificationPopover: !this.state.isOpenNotificationPopover,
    });

    if (!this.state.isNotificationConfirmed) {
      this.setState({ isNotificationConfirmed: true });
    }
  };

  toggleUserCardPopover = () => {
    this.setState({
      isOpenUserCardPopover: !this.state.isOpenUserCardPopover,
    });
  };

  handleSidebarControlButton = event => {
    event.preventDefault();
    event.stopPropagation();

    document.querySelector('.cr-sidebar').classList.toggle('cr-sidebar--open');
  };

  render() {
    const { isNotificationConfirmed } = this.state;

    return (
      <Navbar light expand className={bem.b('bg-white')}>
        <Nav navbar className="mr-2">
          <Button outline onClick={this.handleSidebarControlButton}>
            <MdClearAll size={25} />
          </Button>
        </Nav>
        <NavItem
              className={bem.e('nav-item')}
              onClick={this.handleClick('Components')}
            >
              <BSNavLink className={bem.e('nav-item-collapse')}>
                <div className="d-flex">
                  <MdExtension className={bem.e('nav-item-icon')} />
                  <span className=" align-self-start">Cartes</span>
                </div>
                <MdKeyboardArrowDown
                  className={bem.e('nav-item-icon')}
                  style={{
                    padding: 0,
                    transform: this.state.isOpenComponents
                      ? 'rotate(0deg)'
                      : 'rotate(-90deg)',
                    transitionDuration: '0.3s',
                    transitionProperty: 'transform',
                  }}
                />
              </BSNavLink>
            </NavItem>

        <Nav navbar className={bem.e('nav-right')}>
          {this.isUser() &&
            <NavItem className="d-inline-flex">
              <NavLink id="Popover1" className="position-relative">
                {isNotificationConfirmed ? (
                  <MdNotificationsNone
                    size={25}
                    className="text-secondary can-click"
                    onClick={this.toggleNotificationPopover}
                  />
                ) : (
                    <MdNotificationsActiveWithBadge
                      size={25}
                      className="text-secondary can-click animated swing infinite"
                      onClick={this.toggleNotificationPopover}
                    />
                  )}
              </NavLink>
              <Popover
                placement="bottom"
                isOpen={this.state.isOpenNotificationPopover}
                toggle={this.toggleNotificationPopover}
                target="Popover1"
              >
                <PopoverBody>
                  <Notifications notificationsData={this.state.notificationsData} />
                </PopoverBody>
              </Popover>
            </NavItem>}

            <Collapse isOpen={this.state.isOpenComponents}>
              {navComponents.map(({ to, name, exact, Icon }, index) => (
                <NavItem key={index} className={bem.e('nav-item')}>
                  <BSNavLink
                    id={`navItem-${name}-${index}`}
                    className="text-uppercase"
                    tag={NavLink}
                    to={to}
                    activeClassName="active"
                    exact={exact}
                  >
                    <Icon className={bem.e('nav-item-icon')} />
                    <span className="">{name}</span>
                  </BSNavLink>
                </NavItem>
              ))}
            </Collapse>

          <NavItem>
            <NavLink id="Popover2">
              <Avatar
                onClick={this.toggleUserCardPopover}
                className="can-click"
              />
            </NavLink>
            <Popover
              placement="bottom-end"
              isOpen={this.state.isOpenUserCardPopover}
              toggle={this.toggleUserCardPopover}
              target="Popover2"
              className="p-0 border-0"
              style={{ minWidth: 250 }}
            >
              <PopoverBody className="p-0 border-light">
                <UserCard
                  title={this.state.first_name}
                  subtitle={this.state.email}
                  // text="Last updated 3 mins ago"
                  className="border-light"
                >
                  <ListGroup flush>
                    <Link to="/profile">
                      <ListGroupItem tag="button" action className="border-light">
                        <MdPersonPin /> Profil
                    </ListGroupItem>
                    </Link>
                    <ListGroupItem tag="button" action className="border-light" onClick={this.logOut.bind(this)}>
                      <MdExitToApp /> Déconnexion
                    </ListGroupItem>
                  </ListGroup>
                </UserCard>
              </PopoverBody>
            </Popover>
          </NavItem>
        </Nav>
      </Navbar>
    );
  }
}

export default withRouter(Header);
