import axios from 'axios'

export const register = newUser => {
  return axios
    .post('users/register', {
      first_name: newUser.first_name,
      last_name: newUser.last_name,
      email: newUser.email,
      password: newUser.password,
      class: newUser.class,
    })
    .then(response => {
      console.log('Registered')
    })
}

export const update = newUser => {
  return axios
    .post('users/update', {
      _id: newUser._id,
      first_name: newUser.first_name,
      last_name: newUser.last_name,
      email: newUser.email,
      password: newUser.password,
    })
    .then(response => {
      console.log('Updated')
      //localStorage.setItem('usertoken', response.data)
      // return response.data
    })
}

export const login = user => {
  return axios
    .post('users/login', {
      email: user.email,
      password: user.password
    })
    .then(response => {
      localStorage.setItem('usertoken', response.data)
      return response.data
    })
    .catch(err => {
      console.log(err)
    })
}

export const getProfile = user => {
  return axios
    .get('users/profile', {
      //headers: { Authorization: ` ${this.getToken()}` }
    })
    .then(response => {
      console.log(response)
      return response.data
    })
    .catch(err => {
      console.log(err)
    })

}


export const getAllModels = model => {
  return axios
    .get('models/getModels', {
      //headers: { Authorization: ` ${this.getToken()}` }
    })
    .then(response => {
      console.log(response)
      return response.data.cardModel
    })
    .catch(err => {
      console.log(err)
    })
}

export const getCardsByModel = cards => {
  return axios
    .get('cards/getCardsByModel')
    .then(response => {
      console.log(response)
      return response.data.cards
    })
    .catch(err => {
      console.log(err)
    })
}

export const getCards = cards => {
  return axios
    .get('cards/getAllCards')
    .then(response => {
      console.log(response)
      return response.data.card
    })
    .catch(err => {
      console.log(err)
    })}

export const reserve = cards => {
  return axios.get('reservations/add')  // for allPosts
.then(response => {
  console.log(response)
  return axios.get('cards/updateStatus'); 
  // using response.data
})
.then((response) => {
  console.log('Response', response);
});
}
