import bg11Image from 'assets/img/bg/background_1920-11.jpg';
import bg18Image from 'assets/img/bg/background_1920-18.jpg';
import bg1Image from 'assets/img/bg/background_640-1.jpg';
import bg3Image from 'assets/img/bg/background_640-3.jpg';
import raspImage from 'assets/img/products/rasp.jpg';
import { UserCard } from 'components/Card';
import Page from 'components/Page';
import { bgCards, gradientCards, overlayCards } from 'demos/cardPage';
import { getStackLineChart, stackLineChartOptions } from 'demos/chartjs';
import React from 'react';
import { Line } from 'react-chartjs-2';
import axios from 'axios';
import {
  Button,
  Card,
  CardBody,
  CardImg,
  CardImgOverlay,
  CardLink,
  CardText,
  CardTitle,
  Col,
  ListGroup,
  ListGroupItem,
  Modal,
  ModalBody,
  ModalFooter,
  ModalHeader,
  Row,
} from 'reactstrap';
import { getAllModels } from 'components/UserFunctions';
class CardPage extends React.Component {
  constructor() {
    super()
    this.state = {
      models: [],
      modal: false,
      modal_backdrop: false,
      modal_nested_parent: false,
      modal_nested: false,
      backdrop: true,
    }
  }
  
  toggle = modalType => () => {
    if (!modalType) {
      return this.setState({
        modal: !this.state.modal,
      });
    }

    this.setState({
      [`modal_${modalType}`]: !this.state[`modal_${modalType}`],
    });
  };
  componentDidMount() {
    /*  let models = [...this.state.model]
      getAllModels().then(data =>  models.push(data),
      this.setState({
        model: models
      }))*/
    getAllModels()
      .then(res => {
        const models = res;
        this.setState({ models });
      })
  }

  render() {
    return (
      <Page title="Cartes" breadcrumbs={[{ name: 'Cartes', active: true }]}>
        <Row>
          <Col md={6} sm={6} xs={12} className="mb-3">
            <Card>
              <CardBody>
                <CardTitle>Les modéles disponibles sur notre plateforme</CardTitle>
                <CardText>
                 Contenu
              </CardText>
              </CardBody>
            </Card>
          </Col>
        </Row>
        <Row>
          {this.state.models.map(model => {

            return (
              <Col md={5} key={model._id} >
                <UserCard
                  avatar={raspImage}
                  title={model.name}
                  text={model.name}
                  style={{
                    height: 300,
                  }}

                >
                  <Button outline color="light"  onClick={this.toggle()}>
                    Voir Plus
                </Button>
                <Modal
                  isOpen={this.state.modal}
                  toggle={this.toggle()}
                  className={this.props.className}>
                  <ModalHeader toggle={this.toggle()}>{model.name}</ModalHeader>
                  <ModalBody>
                    Lorem ipsum dolor sit amet, consectetur adipisicing elit,
                    sed do eiusmod tempor incididunt ut labore et dolore magna
                    aliqua. Ut enim ad minim veniam, quis nostrud exercitation
                    ullamco laboris nisi ut aliquip ex ea commodo consequat.
                    Duis aute irure dolor in reprehenderit in voluptate velit
                    esse cillum dolore eu fugiat nulla pariatur. Excepteur sint
                    occaecat cupidatat non proident, sunt in culpa qui officia
                    deserunt mollit anim id est laborum.
                  </ModalBody>
                  <ModalFooter>
                  <Button color="primary" onClick={this.toggle()}>
                      Réserver
                    </Button>{' '}
                    <Button color="secondary" onClick={this.toggle()}>
                      OK
                    </Button>
                  </ModalFooter>
                </Modal>
                </UserCard>
              </Col>
            );
          })}

        </Row>

      </Page>
    );
  }
}

export default CardPage;
