import logo200Image from 'assets/img/logo/logo_200.png';
import PropTypes from 'prop-types';
import React from 'react';
import { withRouter } from "react-router-dom";
import {
  Button,
  Card,
  CardBody,
  CardHeader,
  Col,
  Form,
  FormFeedback,
  FormGroup,
  FormText,
  Input,
  Label,
  Row,
} from 'reactstrap';
import jwt_decode from 'jwt-decode';
import { register } from './UserFunctions';
import { login } from './UserFunctions';

class IotTest extends React.Component {
  constructor() {
    super()
    this.state = {
      first_name: '',
      last_name: '',
      email: '',
      password: '',
      errors: {}
    }

    this.onChange = this.onChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  get isLogin() {
    return this.props.authState === STATE_LOGIN;
  }

  get isSignup() {
    return this.props.authState === STATE_SIGNUP;
  }
  onChange(e) {
    this.setState({ [e.target.name]: e.target.value })
  }
  changeAuthState = authState => event => {
    event.preventDefault();

    this.props.onChangeAuthState(authState);
  };
  componentDidMount() {
    const token = localStorage.usertoken
    const decoded = jwt_decode(token)
    this.setState({
      first_name: decoded.first_name,
      last_name: decoded.last_name,
      email: decoded.email
    })
  }

  handleSubmit = event => {
    event.preventDefault();
    const newUser = {
      first_name: this.state.first_name,
      last_name: this.state.last_name,
      email: this.state.email,
      password: this.state.password,
      class: this.state.class
    }
    const user = {
      email: this.state.email,
      password: this.state.password
    }
    {
      this.isSignup ? register(newUser).then(res => {
        //this.changeAuthState(STATE_LOGIN)
        this.props.history.push(`/login`)
      }) : login(user).then(res => {
        if (res) {
         this.props.history.push(`/home`)
        }
      })
    }

  };

  renderButtonText() {
    const { buttonText } = this.props;

    if (!buttonText && this.isLogin) {
      return 'Connexion';
    }

    if (!buttonText && this.isSignup) {
      return 'Inscription';
    }

    return buttonText;
  }

  render() {
    const {
      showLogo,
      firstnameLabel,
      firstnameInputProps,
      lastnameLabel,
      lastnameInputProps,
      usernameLabel,
      usernameInputProps,
      passwordLabel,
      passwordInputProps,
      confirmPasswordLabel,
      confirmPasswordInputProps,
      classLabel,
      classInputProps,
      children,
      onLogoClick,
    } = this.props;

    return (
        
          <Row>
            <Col xl={8} lg={12} md={100} marginWidth={12}>
              <Card>
                <CardHeader>Profil</CardHeader>
                <CardBody>
                  <Form>
                  <FormGroup row>
                      <Label for="exampleEmail" sm={2}>
                        Nom
                      </Label>
                      <Col sm={10}>
                        <Input
                          type="text"
                          name="last_name"
                          value={this.state.last_name}
                        />
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Label for="exampleEmail" sm={2}>
                       Prénom
                      </Label>
                      <Col sm={10}>
                        <Input
                          type="text"
                          name="first_name"
                          value={this.state.first_name}
                        />
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Label for="exampleEmail" sm={2}>
                        Adresse mail
                      </Label>
                      <Col sm={10}>
                        <Input
                          type="email"
                          name="email"
                          value={this.state.email}
                        />
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Label for="examplePassword" sm={2}>
                        Mot de passe
                      </Label>
                      <Col sm={10}>
                        <Input
                          type="password"
                          name="password"
                          value={this.state.password}
                        />
                      </Col>
                    </FormGroup>
                   
                    <FormGroup row>
                      <Label for="exampleFile" sm={2}>
                       Changer Image
                      </Label>
                      <Col sm={10}>
                        <Input type="file" name="file" />
                        <FormText color="muted">
                         Vous pouvez importer un fichier de format .jpg ou .png comme étant 
                         une photo de profil
                        </FormText>
                      </Col>
                    </FormGroup>
                
                
                    <FormGroup check row>
                      <Col sm={{ size: 10, offset: 2 }}>
                        <Button>Enregister Modifications</Button>
                      </Col>
                    </FormGroup>
                  </Form>
                </CardBody>
              </Card>
            </Col>
          </Row>
  
      );
  }
}



export default withRouter(IotTest);
