import logo200Image from 'assets/img/logo/logo_200.png';
import PropTypes from 'prop-types';
import React from 'react';
import { Button, Form, FormGroup, Input, Label } from 'reactstrap';
import { withRouter } from "react-router-dom";

class AuthForm extends React.Component {
  constructor() {
    super()
    this.state = {
      first_name: '',
      last_name: '',
      email: '',
      password: '',
      class: '',
      errors: {}
    }

    this.onChange = this.onChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  get isLogin() {
    return this.props.authState === STATE_LOGIN;
  }

  get isSignup() {
    return this.props.authState === STATE_SIGNUP;
  }
  onChange(e) {
    this.setState({ [e.target.name]: e.target.value })
  }
  changeAuthState = authState => event => {
    event.preventDefault();

    this.props.onChangeAuthState(authState);
  };

  handleSubmit = event => {
    event.preventDefault();
    const newUser = {
      first_name: this.state.first_name,
      last_name: this.state.last_name,
      email: this.state.email,
      password: this.state.password,
      class: this.state.class
    }
    const user = {
      email: this.state.email,
      password: this.state.password
    }
    {
      this.isSignup ? register(newUser).then(res => {
        //this.changeAuthState(STATE_LOGIN)
        this.props.history.push(`/login`)
      }) : login(user).then(res => {
        if (res) {
         this.props.history.push(`/home`)
        }
      })
    }

  };

  renderButtonText() {
    const { buttonText } = this.props;

    if (!buttonText && this.isLogin) {
      return 'Connexion';
    }

    if (!buttonText && this.isSignup) {
      return 'Inscription';
    }

    return buttonText;
  }

  render() {
    const {
      showLogo,
      firstnameLabel,
      firstnameInputProps,
      lastnameLabel,
      lastnameInputProps,
      usernameLabel,
      usernameInputProps,
      passwordLabel,
      passwordInputProps,
      confirmPasswordLabel,
      confirmPasswordInputProps,
      classLabel,
      classInputProps,
      children,
      onLogoClick,
    } = this.props;

    return (
        <Page title="Profil" breadcrumbs={[{ name: 'Profil', active: true }]}>
          <Row>
          
    
            <Col xl={6} lg={12} md={12}>
              <Card>
                <CardHeader>Form Grid</CardHeader>
                <CardBody>
                  <Form>
                    <FormGroup row>
                      <Label for="exampleEmail" sm={2}>
                        Email
                      </Label>
                      <Col sm={10}>
                        <Input
                          type="email"
                          name="email"
                          placeholder="with a placeholder"
                        />
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Label for="examplePassword" sm={2}>
                        Password
                      </Label>
                      <Col sm={10}>
                        <Input
                          type="password"
                          name="password"
                          placeholder="password placeholder"
                        />
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Label for="exampleSelect" sm={2}>
                        Select
                      </Label>
                      <Col sm={10}>
                        <Input type="select" name="select" />
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Label for="exampleSelectMulti" sm={2}>
                        Select Multiple
                      </Label>
                      <Col sm={10}>
                        <Input type="select" name="selectMulti" multiple />
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Label for="exampleText" sm={2}>
                        Text Area
                      </Label>
                      <Col sm={10}>
                        <Input type="textarea" name="text" />
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Label for="exampleFile" sm={2}>
                        File
                      </Label>
                      <Col sm={10}>
                        <Input type="file" name="file" />
                        <FormText color="muted">
                          This is some placeholder block-level help text for the
                          above input. It's a bit lighter and easily wraps to a new
                          line.
                        </FormText>
                      </Col>
                    </FormGroup>
                    <FormGroup tag="fieldset" row>
                      <Label for="checkbox2" sm={2}>
                        Radio
                      </Label>
                      <Col sm={10}>
                        <FormGroup check>
                          <Label check>
                            <Input type="radio" name="radio2" /> Option one is this
                            and that—be sure to include why it's great
                          </Label>
                        </FormGroup>
                        <FormGroup check>
                          <Label check>
                            <Input type="radio" name="radio2" /> Option two can be
                            something else and selecting it will deselect option one
                          </Label>
                        </FormGroup>
                        <FormGroup check disabled>
                          <Label check>
                            <Input type="radio" name="radio2" disabled /> Option
                            three is disabled
                          </Label>
                        </FormGroup>
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Label for="checkbox2" sm={2}>
                        Checkbox
                      </Label>
                      <Col sm={{ size: 10 }}>
                        <FormGroup check>
                          <Label check>
                            <Input type="checkbox" id="checkbox2" /> Check me out
                          </Label>
                        </FormGroup>
                      </Col>
                    </FormGroup>
                    <FormGroup check row>
                      <Col sm={{ size: 10, offset: 2 }}>
                        <Button>Submit</Button>
                      </Col>
                    </FormGroup>
                  </Form>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </Page>
      );
  }
}

export const STATE_LOGIN = 'LOGIN';
export const STATE_SIGNUP = 'SIGNUP';

AuthForm.propTypes = {
