import Page from 'components/Page';
import React from 'react';
import { Link, withRouter } from 'react-router-dom'
import { getCards } from 'components/UserFunctions';
import {
    Button,
    Card,
    CardBody,
    CardHeader,
    Col,
    Modal,
    ModalBody,
    ModalFooter,
    ModalHeader,
    Row, Table
} from 'reactstrap';
class CardList extends React.Component {
    constructor() {
        super()
        this.state = {
            cards: [],
            modal: false,
            modal_backdrop: false,
            modal_nested_parent: false,
            modal_nested: false,
            backdrop: true,
        }
    }

    toggle = modalType => () => {
        if (!modalType) {
            return this.setState({
                modal: !this.state.modal,
            });
        }

        this.setState({
            [`modal_${modalType}`]: !this.state[`modal_${modalType}`],
        });
    };
    componentDidMount() {
        /*  let models = [...this.state.model]
          getAllModels().then(data =>  models.push(data),
          this.setState({
            model: models
          }))*/
          getCards()
            .then(res => {
                const cards = res;
                this.setState({ cards });
            })
    }

    render() {
        return (
            <Page title="Utilisateurs" breadcrumbs={[{ name: 'Liste Users', active: true }]}>

                <Row>
                    <Col>
                        <Card className="mb-3">
                            <CardHeader>IRTL Cards</CardHeader>
                            <CardBody>
                                <Table striped>
                                    <thead>
                                        <tr>
                                            <th>#</th>
                                            <th>Nom</th>
                                            <th>Modéle</th>
                                            <th>Date d'ajout</th>
                                            <th>Statut</th>
                                            <th></th>
                                            <th></th>
                                        </tr>
                                    </thead>
                                    <tbody>

                                        {this.state.cards.map(user => {

                                            return (
                                                <tr>
                                                    <th scope="row"></th>
                                                    <td>{user.name}</td>
                                                    <td>{user.model}</td>
                                                    <td>{user.date}</td>
                                                    <td>{user.status ? "Disponible":"Indisponible"}</td>
                                                    <td><Button color="danger">Supprimer</Button></td>
                                                    <td><Button color="info">Editer</Button></td>
                                                </tr>);
                                        })}

                                    </tbody>
                                </Table>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>    <Link to="/addCard">
<Button color="primary" >Ajouter une Carte</Button></Link>
            </Page>
        );
    }
}

export default CardList;
