import Page from 'components/Page';
import carte from 'assets/img/products/raspberry pi.PNG';
import PropTypes from 'prop-types';
import React from 'react';
import { addPattern ,getPatterns} from 'components/UserFunctions';
import { withRouter,Link } from "react-router-dom";
import {
  Button,
  Card,
  CardBody,
  CardHeader,
  Col,
  Row,
  CardText,
  Modal,
  ModalBody,
  ModalFooter,
  ModalHeader,
} from 'reactstrap';
class PatternPage extends React.Component {
  constructor() {
    super()
    this.state = {
      patterns: [],
      first_name: '',
      last_name: '',
      email: '',
      password: '',
      errors: {},
      modal: false,
      modal_backdrop: false,
      modal_nested_parent: false,
      modal_nested: false,
    }

    this.onChange = this.onChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  }


  onChange(e) {
    this.setState({ [e.target.name]: e.target.value })
  }
 
  componentDidMount() {
    getPatterns()
    .then(res => {
        const patterns = res;
        this.setState({ patterns });
    })
  
  }
  toggle = modalType => () => {
    if (!modalType) {
      return this.setState({
        modal: !this.state.modal,
      });
    }
    this.setState({
      [`modal_${modalType}`]: !this.state[`modal_${modalType}`],
    });
  };

  handleSubmit = event => {
    event.preventDefault();
 

  };

  redirect(e) {
    e.preventDefault()
    this.props.history.push(`/home`)
  }

  render() {
    

    return (
      <Page title="Utilisateurs" breadcrumbs={[{ name: 'Email Pattern', active: true }]}>
         
         
                  <Row>
            <Col md={6} sm={6} xs={12} className="mb-3">
              <Card>
                <CardHeader>Mes Email Patterns</CardHeader>
                <CardBody>
              <img src={carte} height="300" width="650">
              </img>
                </CardBody>
              </Card>
            </Col>
            <Col md={6} sm={6} xs={12} className="mb-3">
              <Card>
                <CardHeader>Ajouter un nouveau Pattern
               
                          
                          
                </CardHeader>
                <CardBody>
               <textarea style={{ width:650, height: 700, cursor: 'pointer' }}></textarea>
                    <Row cellPadding={12}>
                        <Link to="/home">
                             <Button color="primary">Tester</Button>
                     
                          </Link>
                         
                  </Row>
                </CardBody>
              </Card>
            </Col>
            </Row>
          </Page>
  
      );
  }
}



export default withRouter(PatternPage);
