import bg11Image from 'assets/img/bg/background_1920-11.jpg';
import bg18Image from 'assets/img/bg/background_1920-18.jpg';
import bg1Image from 'assets/img/bg/background_640-1.jpg';
import bg3Image from 'assets/img/bg/background_640-3.jpg';
import raspImage from 'assets/img/products/rasp.jpg';
import { UserCard } from 'components/Card';
import Page from 'components/Page';
import { bgCards, gradientCards, overlayCards } from 'demos/cardPage';
import { getStackLineChart, stackLineChartOptions } from 'demos/chartjs';
import React from 'react';
import PropTypes from 'prop-types';
import { Line } from 'react-chartjs-2';
import axios from 'axios';
import { Link, withRouter } from 'react-router-dom';
import moment from 'moment';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import {
  Button,
  Card,
  CardBody,
  CardImg,
  CardImgOverlay,
  CardLink,
  CardText,
  CardTitle,
  Col,
  Row,
  Form,
  FormFeedback,
  FormGroup,
  FormText,
  Input,
  Label,
  UncontrolledButtonDropdown,
  Modal,
  ModalBody,
  ModalFooter,
  ModalHeader,

} from 'reactstrap';
import jwt_decode from 'jwt-decode';
import { reserve, getCards } from 'components/UserFunctions';
import { mongoose } from 'mongoose';
import fr from 'date-fns/locale/fr';
class ReservationPage extends React.Component {
  constructor() {
    super()
    this.state = {
      startDate: new Date(),
      models: [],
      cards: [],
      card: 0,
      hours: 0,
      modal: false,
      modal_backdrop: false,
      modal_nested_parent: false,
      modal_nested: false,
      backdrop: true,
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.forceUpdateHandler = this.forceUpdateHandler.bind(this);
  }
  handleChange(event) {
    // this.setState({ value: event.target.value });
    // this.setState({ val: event.target.val });
    this.setState({ [event.target.name]: event.target.value })
  }
  onChange = date => this.setState({ startDate: date })
  forceUpdateHandler() {
    this.forceUpdate();
  };
  componentDidMount() {
    /* getAllModels()
       .then(res => {
         const models = res;
         this.setState({ models });
       })*/
    getCards().then(res => {
      const cards = res;
      this.setState({ cards: res });
    })
  }
  toggle = modalType => () => {
    if (!modalType) {
      return this.setState({
        modal: !this.state.modal,
      });
    }
    this.setState({
      [`modal_${modalType}`]: !this.state[`modal_${modalType}`],
    });
  };
  componentDidUpdate(prevProps, prevState) {
    if (prevState.startDate !== this.state.startDate) {
      //console.log(prevState.startDate)
      // console.log(this.state.startDate)
      // const _id=this.state.value
      getCards().then(res => {
        const cards = res;
        this.setState({ card: res });
      });
      // this.classMethod();

    }
  }
  handleSubmit = event => {
    event.preventDefault();
    const token = localStorage.usertoken
    const decoded = jwt_decode(token)
    const newRev = {
      user: decoded._id,
      card: this.state.card,
      hours: this.state.hours,
      begin: this.state.startDate,
      status: false,
    }


    reserve(newRev).then(res => {
      this.props.history.push(`/home`)
    })
  }

  render() {
    return (
      <Page title="Réservation" breadcrumbs={[{ name: 'Réservation', active: true }]}>
        <Form onSubmit={this.handleSubmit}>
          <Row>
            <Col md={6} sm={6} xs={12} className="mb-3">

              <Card>
                <CardBody>
                  <CardTitle>Choisir La date</CardTitle>
                  <CardText>
                  <FormGroup row>
                    <DatePicker
                      selected={this.state.startDate}
                      onChange={this.onChange}
                      showTimeSelect
                      timeFormat="HH:mm"
                      timeIntervals={30}
                      timeCaption="Heure"
                      dateFormat="MMMM d, yyyy h:mm aa"
                      minDate={new Date()}
                      locale="fr"
                      placeholderText="Select a date other than today or yesterday"
                      onSelect={this.forceUpdateHandler}
                      value={this.state.startDate}
                    />
                    </FormGroup>
                  </CardText>
                </CardBody>
              </Card>
            </Col>
          </Row>
          <Card>
            <CardBody>

              <Row>

                <Col md={6} sm={6} xs={12} className="mb-3">
                  <Card>
                    <CardBody>
                      <CardTitle>Choisir Une Carte</CardTitle>
                      <FormGroup row>
                        <Label for="exampleSelect" sm={2}>
                          Cartes
                  </Label>

                        <Col sm={10}>
                          <Input type="select" name="card" value={this.state.card} onChange={this.handleChange} >
                            <option value="0"></option>
                            {this.state.cards.map(card => {

                              return (
                                <option value={card._id} disabled={card.status ? false : true}>{card.name} {card.status ? ' (Disponible)' : ' (Indisponible)'}</option>
                              );
                            })}
                          </Input>
                        </Col>
                      </FormGroup>
                    </CardBody>
                  </Card>
                </Col>
                <Col md={6} sm={6} xs={12} className="mb-3">
                  <Card>
                    <CardBody>
                      <CardTitle>Choisir La durée de réservation </CardTitle>
                      <FormGroup row>
                        <Label for="exampleSelect" sm={2}>
                          Durée
                  </Label>
                        <Col sm={10}>
                          <Input type="select" name="hours" color="primary" value={this.state.hours}
                            onChange={this.handleChange} >
                            <option value="1">1 heure </option>
                            <option value="2">2 heures</option>
                            <option value="3">3 heures</option>
                            <option value="4">4 heures</option>
                          </Input>
                        </Col>
                      </FormGroup>
                      <FormGroup check row>
                        <Col sm={{ size: 10, offset: 2 }}>

                        </Col>
                      </FormGroup>
                    </CardBody>
                  </Card>
                </Col>
              </Row>
              <Row>

              </Row>

              <Button color="primary">Confirmer</Button>
              <Modal
                isOpen={this.state.modal}
                toggle={this.toggle()}
                className={this.props.className}>
                <ModalHeader toggle={this.toggle()}>INFO</ModalHeader>
                <ModalBody>
                  Votre session Commence maintenant . Bon courage !
                  </ModalBody>
                <ModalFooter>
                  <Button color="primary" >
                    OK
                    </Button>
                </ModalFooter>
              </Modal>


            </CardBody>
          </Card>
        </Form>
      </Page>
    );
  }
}

export default withRouter(ReservationPage);
