import logo200Image from 'assets/img/users/user.png';
import PropTypes from 'prop-types';
import React from 'react';
import { withRouter } from "react-router-dom";
import {
  Button,
  Card,
  CardBody,
  CardHeader,
  Col,
  Form,
  FormFeedback,
  FormGroup,
  FormText,
  Input,
  Label,
  Row,
} from 'reactstrap';
import jwt_decode from 'jwt-decode';
import { update } from './UserFunctions';
import axios from 'axios';

class Profile extends React.Component {
  constructor() {
    super()
    this.state = {
      first_name: '',
      last_name: '',
      email: '',
      password: '',
      profileImg: '',
      errors: {}
    }
    this.onFileChange = this.onFileChange.bind(this);
    this.onChange = this.onChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  onChange(e) {
    this.setState({ [e.target.name]: e.target.value })
  }
  onFileChange(e) {
    this.setState({ profileImg: e.target.files[0] })
}
 
  componentDidMount() {
    const token = localStorage.usertoken
    const decoded = jwt_decode(token)
    this.setState({
      first_name: decoded.first_name,
      last_name: decoded.last_name,
      email: decoded.email,
      password:decoded.password,

    })
  }

  handleSubmit = event => {
    event.preventDefault();
    /*const token = localStorage.usertoken
    const decoded = jwt_decode(token)
    const newUser = {
      _id:decoded._id,
      first_name: this.state.first_name,
      last_name: this.state.last_name,
      email: this.state.email,
      password: this.state.password,
    }
    console.log(newUser._id)
 
      update(newUser).then(res => {
        this.props.history.push(`/home`)
      })*/
      const formData = new FormData()
      formData.append('profileImg', this.state.profileImg)
      axios.post("http://localhost:5000/users/user-profile", formData, {
        first_name:this.state.first_name,
        last_name:this.state.last_name,
        email:this.state.email,
        password:this.state.password
      }).then(res => {
          console.log(res)
          this.props.history.push(`/home`)
      }) 
    

  };

  renderButtonText() {
    const { buttonText } = this.props;

    if (!buttonText && this.isLogin) {
      return 'Connexion';
    }

    if (!buttonText && this.isSignup) {
      return 'Inscription';
    }

    return buttonText;
  }

  render() {
    const {
      showLogo,
      firstnameLabel,
      firstnameInputProps,
      lastnameLabel,
      lastnameInputProps,
      usernameLabel,
      usernameInputProps,
      passwordLabel,
      passwordInputProps,
      confirmPasswordLabel,
      confirmPasswordInputProps,
      classLabel,
      classInputProps,
      children,
      onLogoClick,
    } = this.props;

    return (
      <Row
        style={{
          height: '100vh',
          justifyContent: 'center',
          alignItems: 'center',
        }}>
        <Col md={6} lg={4}>
          <Card body>
            <Form onSubmit={this.handleSubmit} >
              {showLogo && (
                <div className="text-center pb-4">
                  <img
                    src={logo200Image}
                    className="rounded"
                    style={{ width: 80, height: 80, cursor: 'pointer' }}
                    alt="logo"
                    onClick={onLogoClick}
                  />
                </div>
              )}
              <FormGroup row>
                <Label for="exampleEmail" sm={2}>
                  Nom
                      </Label>
                <Col sm={10}>
                  <Input
                    type="text"
                    name="last_name"
                    value={this.state.last_name}
                    onChange={this.onChange}
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label for="exampleEmail" sm={2}>
                  Prénom
                      </Label>
                <Col sm={10}>
                  <Input
                    type="text"
                    name="first_name"
                    value={this.state.first_name}
                    onChange={this.onChange}
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label for="exampleEmail" sm={2}>
                  Adresse mail
                      </Label>
                <Col sm={10}>
                  <Input
                    type="email"
                    name="email"
                    value={this.state.email}
                    onChange={this.onChange}
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label for="examplePassword" sm={2}>
                  Mot de passe
                      </Label>
                <Col sm={10}>
                  <Input
                    type="password"
                    name="password"
                    value={this.state.password}
                    onChange={this.onChange}
                  />
                </Col>
              </FormGroup>

              <FormGroup row>
                <Label for="exampleFile" sm={2}>
                  Changer Image
                      </Label>
                <Col sm={10}>
                  <Input type="file" name="file" onChange={this.onFileChange} />
                  <FormText color="muted">
                    Vous pouvez importer un fichier de format .jpg ou .png comme étant
                    une photo de profil
                        </FormText>
                </Col>
              </FormGroup>


              <FormGroup check row>
                <Col sm={{ size: 10, offset: 2 }}>
                  <Button>Enregister Modifications</Button>
                </Col>
              </FormGroup>
            </Form>
          </Card>
        </Col>
      </Row>



    );
  }
}

export const STATE_LOGIN = 'LOGIN';
export const STATE_SIGNUP = 'SIGNUP';

Profile.propTypes = {
  authState: PropTypes.oneOf([STATE_LOGIN, STATE_SIGNUP]).isRequired,
  showLogo: PropTypes.bool,
  usernameLabel: PropTypes.string,
  usernameInputProps: PropTypes.object,
  firstnameLabel: PropTypes.string,
  firstnameInputProps: PropTypes.object,
  lastnameLabel: PropTypes.string,
  lastnameInputProps: PropTypes.object,
  passwordLabel: PropTypes.string,
  passwordInputProps: PropTypes.object,
  confirmPasswordLabel: PropTypes.string,
  confirmPasswordInputProps: PropTypes.object,
  classLabel: PropTypes.string,
  classInputProps: PropTypes.object,
  onLogoClick: PropTypes.func,
};

Profile.defaultProps = {
  authState: 'LOGIN',
  showLogo: true,
  firstnameLabel: 'Prénom',
  firstnameInputProps: {
    type: 'text',
    placeholder: 'Votre Prénom',

  },
  lastnameLabel: 'Nom',
  lastnameInputProps: {
    type: 'text',
    placeholder: 'Votre Nom',
  },
  usernameLabel: 'Email',
  usernameInputProps: {
    type: 'email',
    placeholder: 'your@email.com',
  },
  passwordLabel: 'Mot de passe',
  passwordInputProps: {
    type: 'password',
    placeholder: 'Votre mot de passe',
  },
  confirmPasswordLabel: 'Confirmer Mot de passe',
  confirmPasswordInputProps: {
    type: 'password',
    placeholder: 'Confirmer Votre mot de passe',
  },
  classLabel: 'Classe',
  classInputProps: {
    type: 'text',
    placeholder: 'Votre Classe (exp 1A1)',
  },
  onLogoClick: () => { },
};

export default withRouter(Profile);
