import raspImage from 'assets/img/products/rasp.jpg';
import { UserCard } from 'components/Card';
import Page from 'components/Page';
import React from 'react';
import { Link, withRouter } from 'react-router-dom'
import {
  Button,
  Card,
  CardBody,
  CardImg,
  CardImgOverlay,
  CardLink,
  CardText,
  CardTitle,
  Col,
  ListGroup,
  ListGroupItem,
  Modal,
  ModalBody,
  ModalFooter,
  ModalHeader,
  Row,
} from 'reactstrap';
import { getAllModels } from 'components/UserFunctions';
class ModelPage extends React.Component {
  constructor() {
    super()
    this.state = {
      models: [],
      modal: false,
      modal_backdrop: false,
      modal_nested_parent: false,
      modal_nested: false,
      backdrop: true,
    }
  }

  toggle = modalType => () => {
    if (!modalType) {
      return this.setState({
        modal: !this.state.modal,
      });
    }

    this.setState({
      [`modal_${modalType}`]: !this.state[`modal_${modalType}`],
    });
  };
  openModal(id) {
    this.setState({
      modal: {
        [id]: true
      }
    });
  }
  componentDidMount() {
    /*  let models = [...this.state.model]
      getAllModels().then(data =>  models.push(data),
      this.setState({
        model: models
      }))*/
    getAllModels()
      .then(res => {
        const models = res;
        this.setState({ models });
      })
  }

  render() {
    return (
      <Page title="Cartes" breadcrumbs={[{ name: 'Modéle', active: true }]}>
        <Row>
          {this.state.models.map(model => {

            return (
              <Col md={5} key={model._id} >
                <UserCard
                  avatar={raspImage}
                  title={model.name}
                  text={model.name}
                  style={{
                    height: 300,
                  }}

                >
                  <Button outline color="light" onClick={this.openModal.bind(this, model._id)}>
                    Voir Plus
                </Button>
                <Modal
                     isOpen={this.state.modal[model._id]}
                     toggle={this.openModal.bind(this, model._id)}
                    className={this.props.className}>
                    <ModalHeader toggle={this.toggle()}>{model.name}</ModalHeader>
                    <ModalBody>
                      Lorem ipsum dolor sit amet, consectetur adipisicing elit,
                      sed do eiusmod tempor incididunt ut labore et dolore magna
                      aliqua. Ut enim ad minim veniam, quis nostrud exercitation
                      ullamco laboris nisi ut aliquip ex ea commodo consequat.
                      Duis aute irure dolor in reprehenderit in voluptate velit
                      esse cillum dolore eu fugiat nulla pariatur. Excepteur sint
                      occaecat cupidatat non proident, sunt in culpa qui officia
                      deserunt mollit anim id est laborum.
                  </ModalBody>
                    <ModalFooter>
                      <Link to="/reservation">
                        <Button color="primary" onClick={this.toggle()}>
                          Supprimer
                    </Button></Link>
                      <Button color="secondary" onClick={this.toggle()}>
                        Editer
                    </Button>
                    </ModalFooter>
                  </Modal>
                </UserCard>
             
              </Col>
              
            );
          })}

        </Row>
        <Link to="/addModel">
<Button color="primary" >Ajouter un modéle</Button></Link>
      </Page>
    );
  }
}

export default ModelPage;
