import Page from 'components/Page';
import React from 'react';
import { Link, withRouter } from 'react-router-dom'
import * as moment from 'moment'
import { getReclamations, checkRec } from 'components/UserFunctions';
import jwt_decode from 'jwt-decode';
import {
    Button,
    Card,
    CardBody,
    CardHeader,
    Col,
    Modal,
    ModalBody,
    ModalFooter,
    ModalHeader,
    Row, Table,
    Form,
    FormGroup,
    FormText,
    Input,
    Label,
} from 'reactstrap';
class ReclamationsList extends React.Component {
    constructor() {
        super()
        this.state = {
            users: [],
            res: '',
            modal: false,
            modal_backdrop: false,
            modal_nested_parent: false,
            modal_nested: false,
            backdrop: true,
            errors: {
                objet: '',
                rec: '',
            }
        }
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    toggle = modalType => () => {
        if (!modalType) {
            return this.setState({
                modal: !this.state.modal,
                
            });
        }

        this.setState({
            [`modal_${modalType}`]: !this.state[`modal_${modalType}`],
        });
    };
    openModal (id) {
        this.setState({
           modal: {
              [id]: true
           }
        });
     }
    handleChange = (event) => {
        event.preventDefault();
        const { name, value } = event.target;
        let errors = this.state.errors;

        switch (name) {
            case 'objet':
                errors.objet =
                    value.length > 3
                        ? ''
                        : 'Vérifier votre Objet !';
                break;
            case 'rec':
                errors.res =
                    value.length > 5
                        ? ''
                        : 'Contenu Réclamation ne doit pas être vide !';
                break;
            default:
                break;
        }
        this.setState({ errors, [name]: value });
    }
    handleSubmit = event => {
        event.preventDefault();
        const token = localStorage.usertoken
        const decoded = jwt_decode(token)
        const newRec = {
          user: decoded._id,
          name: this.state.objet,
          description: this.state.rec,
    
        }
       // if (validateForm(this.state.errors) && this.isEmpty()) {
         // addRec(newRec).then(res => {
          //  window.location.reload();
         // })
      //  }
    
      };
    onDelete(id) {
        const recId = id
        checkRec(recId).then(res => {
            window.location.reload();
        })
    }
    componentDidMount() {
        /*  let models = [...this.state.model]
          getAllModels().then(data =>  models.push(data),
          this.setState({
            model: models
          }))*/
        getReclamations()
            .then(res => {
                const users = res;
                this.setState({ users });
            })
    }

    render() {
        return (
            <Page title="Utilisateurs" breadcrumbs={[{ name: 'Liste Users', active: true }]}>

                <Row>
                    <Col>
                        <Card className="mb-3">
                            <CardHeader>IRTL Reclamations</CardHeader>
                            <CardBody>
                                <Table striped>
                                    <thead>
                                        <tr>
                                            <th>#</th>
                                            <th>Objet</th>
                                            <th>Contenu</th>
                                            <th>Date</th>
                                            <th>user</th>
                                            <th></th>
                                        </tr>
                                    </thead>
                                    <tbody>

                                        {this.state.users.map(user => {

                                            return (
                                                <tr>
                                                    <th scope="row"></th>
                                                    <td>{user.name}</td>
                                                    <td>{user.description}</td>
                                                    <td>{moment(user.date).format('DD/MM/YYYY h:mm a')}</td>
                                                    <td>{user.user.first_name}  {user.user.last_name}</td>
                                                    <td><Button color="info" onClick= {this.openModal.bind(this, user._id)}>Répondre</Button></td>
                                                    <Modal
                                                        isOpen={this.state.modal[user._id]} 
                                                        toggle={this.openModal.bind(this, user._id)}
                                                        className={this.props.className}
                                                        zIndex={user._id}>
                                                        <ModalHeader toggle={this.toggle()}>Répondre {user.user.first_name}  {user.user.last_name} </ModalHeader>
                                                        <ModalBody>
                                                            <Form onSubmit={this.handleSubmit} noValidate >

                                                                <FormGroup row>
                                                                    <Label for="exampleEmail" sm={2}>
                                                                        Objet
                                                                    </Label>
                                                                    <Col sm={10}>
                                                                        <Input
                                                                            type="text"
                                                                            name="objet"
                                                                            value={user.name}
                                                                            onChange={this.handleChange}
                                                                            noValidate
                                                                        />
                                                                        {this.state.errors.objet.length > 0 &&
                                                                            <span style={{ fontSize: 12, color: "red" }}>{this.state.errors.bjet}</span>}
                                                                    </Col>
                                                                </FormGroup>
                                                                <FormGroup row>
                                                                    <Label for="exampleEmail" sm={2}>
                                                                        Votre Réponse
                                                                    </Label>
                                                                    <Col sm={10}>
                                                                        <textarea style={{ width: 400, height: 200 }}
                                                                            type="text"
                                                                            name="rec"
                                                                            value={this.state.res}
                                                                            onChange={this.handleChange}
                                                                            noValidate
                                                                        />
                                                                        {this.state.errors.rec.length > 0 &&
                                                                            <span style={{ fontSize: 12, color: "red" }}>{this.state.errors.rec}</span>}
                                                                    </Col>
                                                                </FormGroup>
                                                                <FormGroup check row>
                                                                    <Col sm={{ size: 10, offset: 2 }}>
                                                                        <Button>Envoyer</Button>
                                                                    </Col>
                                                                </FormGroup>
                                                            </Form>
                                                        </ModalBody>
                                                        <ModalFooter>
                                                            <Button color="secondary" onClick={this.toggle()}>
                                                                Annuler
                                                           </Button>
                                                        </ModalFooter>
                                                    </Modal>
                                                 
                                                </tr>
                                                );
                                                
                                        })}

                                    </tbody>
                                </Table>
                                <Link to="/historyReclamations">
                                    <Button color="primary">historique</Button>
                                </Link>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Page>
        );
    }
}

export default ReclamationsList;
