import Page from 'components/Page';
import React from 'react';
import { Link, withRouter } from 'react-router-dom'
import { getCards, deleteCard } from 'components/UserFunctions';
import {
    Button,
    Card,
    CardBody,
    CardHeader,
    Col,
    Modal,
    ModalBody,
    ModalFooter,
    ModalHeader,
    Row, Table
} from 'reactstrap';
class CardList extends React.Component {
    constructor() {
        super()
        this.state = {
            cards: [],
            modal: false,
            modal_backdrop: false,
            modal_nested_parent: false,
            modal_nested: false,
            backdrop: true,
            change:false,
        }
    }

    toggle = modalType => () => {
        if (!modalType) {
            return this.setState({
                modal: !this.state.modal,
            });
        }

        this.setState({
            [`modal_${modalType}`]: !this.state[`modal_${modalType}`],
        });

    };
    onDelete(id) { 
        const cardId=id
        deleteCard(cardId).then(res => {
            this.setState({change:true})
          })
    }
    componentDidMount() {
        /*  let models = [...this.state.model]
          getAllModels().then(data =>  models.push(data),
          this.setState({
            model: models
          }))*/
        getCards()
            .then(res => {
                const cards = res;
                this.setState({ cards });
            })
    }
     componentDidUpdate(prevProps, prevState) {
     if (prevState.change !== this.state.change) {
       console.log(prevState.change)
       console.log(this.state.change)
       getCards().then(res => {
         const cards = res;
         this.setState({ card:res});
       });
     
     }
   }

    render() {
        return (
            <Page title="Cartes" breadcrumbs={[{ name: 'Liste Cartes', active: true }]}>

                <Row>
                    <Col>
                        <Card className="mb-3">
                            <CardHeader>IRTL Cards</CardHeader>
                            <CardBody>
                                <Table striped>
                                    <thead>
                                        <tr>
                                            <th>#</th>
                                            <th>Nom</th>
                                            <th>Date d'ajout</th>
                                            <th>Statut</th>
                                            <th></th>
                                            <th></th>
                                        </tr>
                                    </thead>
                                    <tbody>

                                        {this.state.cards.map(user => {

                                            return (
                                                <tr>
                                                    <th scope="row"></th>
                                                    <td>{user.name}</td>
                                                    <td>{user.date}</td>
                                                    <td>{user.status ? "Disponible" : "Indisponible"}</td>
                                                    <td><Button color="danger" onClick={() => this.onDelete(user._id)}>Supprimer</Button></td>
                                                    <td><Button color="info">Editer</Button></td>
                                                </tr>);
                                        })}

                                    </tbody>
                                </Table>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>    <Link to="/addCard">
                    <Button color="primary" >Ajouter une Carte</Button></Link>
            </Page>
        );
    }
}

export default CardList;
